def find_common_item1(compartment1, compartment2):
    # Loop through each item in the first compartment
    for item in compartment1:
      # Check if the item is also in the second compartment
      if item in compartment2: # Case sensitive
        print("found matching item:", item)
        # Calculate & return the priority of the item
        if item.islower():
          return ord(item) - ord('a') + 1
        else:
          return ord(item) - ord('A') + 27
        # Break out of the loop since we found the first common item
        

def count_total_sum1(rucksacks):
  total_sum = 0

  # Loop through each rucksack
  for rucksack in rucksacks:
    # Split the rucksack into its two compartments
    first_compartment = rucksack[:len(rucksack)//2]
    second_compartment = rucksack[len(rucksack)//2:]
    
    # Add the priority to the total sum
    total_sum += find_common_item1(first_compartment, second_compartment)

  return total_sum

