# ----- PART 2 -----
group_size = 3

def find_common_item2(compartments):
    priority = 0
    
    # Loop through each item in the first compartment
    for item in compartments[0]:
      # Check if the item is also in other compartments
      if all(item in compartment for compartment in compartments): # Case sensitive
        print("found matching item:", item)
        # Calculate & return the priority of the item
        if item.islower():
          priority = ord(item) - ord('a') + 1
        else:
          priority = ord(item) - ord('A') + 27
        break
        # Break out of the loop since we found the first common item

    print("item priority:", priority)
    return priority
  
        
def find_common_item_per_group(rucksacks):
    total_sum = 0
    group = []

    for index, rucksack in enumerate(rucksacks):
        group.append(rucksack)
        
        if len(group) % group_size == 0:
            print("--- Group ---")
            total_sum += find_common_item2(group)
            group.clear()

    return total_sum

